INSERT INTO `abc_product_descriptions` (`product_id`, `language_id`, `name`, `meta_keywords`, `meta_description`, `description`, `blurb`) 
                        VALUES ('69', '1', 'Seaweed Conditioner', '', '', '&lt;p&gt;\r\n\tWhat it is:&lt;br /&gt;\r\n\tA lightweight detangler made with marine seaweed and spirulina.&lt;/p&gt;\r\n&lt;p&gt;\r\n\tWhat it does:&lt;br /&gt;\r\n\tThis conditioner gently detangles, nourishes, softens, and helps to manage freshly washed hair.&lt;/p&gt;\r\n&lt;p&gt;\r\n\tWhat it is formulated WITHOUT:&lt;br /&gt;\r\n\t- Parabens&lt;/p&gt;\r\n&lt;p&gt;\r\n\tWhat else you need to know:&lt;br /&gt;\r\n\tMade with marine greens for practically anyone (and ideal for frequent bathers), this conditioner is best paired with Seaweed Shampoo. It\'s also safe for use on color-treated hair.&lt;/p&gt;\r\n&lt;p&gt;\r\n\t-Sea Silk Extract: Nourishes scalp; promotes healthy looking hair.&lt;br /&gt;\r\n\t-Ascophyllum Nudosum (Seaweed) Extract: Moisturizes; adds elasticity, luster, softness, body; reduces flyaways.&lt;br /&gt;\r\n\t-Macrocystis Pyrifera (Sea Kelp) Extract: Adds shine and manageability.&lt;br /&gt;\r\n\t-Spirulina Maxima Extract: Hydrates.&lt;/p&gt;\r\n', '');
