INSERT INTO `abc_product_descriptions` (`product_id`, `language_id`, `name`, `meta_keywords`, `meta_description`, `description`, `blurb`) 
                        VALUES ('116', '1', 'New Ladies High Wedge Heel Toe Thong Diamante Flip Flop Sandals', '', '', '&lt;p&gt;The annual Summer trend for wedges is back with these classic flip flop wedge heels. The sandals have a toe thong design, diamante detailing to the front and look fab worn with a maxi dress for a classic Summer look. Available in a choice of five different colours.&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;Available in 5 different colours&lt;/li&gt;\r\n\t&lt;li&gt;Diamante detailing to the front&lt;/li&gt;\r\n\t&lt;li&gt;Outer Material: &lt;em&gt;Synthetic&lt;/em&gt;&lt;/li&gt;\r\n\t&lt;li&gt;Inner Material: &lt;em&gt;Manmade&lt;/em&gt;&lt;/li&gt;\r\n\t&lt;li&gt;Heel Height: &lt;em&gt;9.5 centimetres&lt;/em&gt;&lt;/li&gt;\r\n\t&lt;li&gt;Heel Type: Wedge&lt;/li&gt;\r\n\t&lt;li&gt;Sandals have a toe thong design&lt;/li&gt;\r\n\t&lt;li&gt;Classic flip flop wedge heels&lt;/li&gt;\r\n\t&lt;li&gt;&lt;strong&gt;Perfect for the Summer!&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '');
INSERT INTO `abc_product_descriptions` (`product_id`, `language_id`, `name`, `meta_keywords`, `meta_description`, `description`, `blurb`) 
                        VALUES ('117', '1', 'Ruby Shoo Womens Jada T-Bar', '', '', '&lt;p&gt;Ruby Shoo is made up of a small team based in north London, England, with a passion for creating unique shoes and accessories that generate smiles whenever they are worn. We simply love it when we are asked the question: &amp;quot;Where did you get your shoes?&amp;quot;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Occasion footwear&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Flower detail&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Outer Material: Synthetic&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Inner Material: Manmade&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Sole: Manmade&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Closure: Buckle&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Heel Height: 9 centimetres&lt;/li&gt;\r\n\t&lt;li style=&quot;list-style-type: square; margin-left: 20px;&quot;&gt;Heel Type: Stiletto&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '');
INSERT INTO `abc_product_descriptions` (`product_id`, `language_id`, `name`, `meta_keywords`, `meta_description`, `description`, `blurb`) 
