(1727, 1, 1, 'common_quick_start', 'text_quick_start', 'Quick Start Wizard', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1728, 1, 1, 'common_quick_start', 'text_quick_start_note', '    	Welcome to AbanteCart administration.<br />\n    	This is a wizard to help you quickly set up your shopping cart with most common settings.\n		<br />\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1729, 1, 1, 'common_quick_start', 'text_quick_start_general', '    	Below are most common general settings. \n    	More settings are available <a href=\"%s\" target=\"_new_admin\"><i class=\"fa fa-link fa-fw\"></i></a>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1730, 1, 1, 'common_quick_start', 'text_quick_start_details', '    	Below are most common settings about your store. \n    	More settings are available <a href=\"%s\" target=\"_new_admin\"><i class=\"fa fa-link fa-fw\"></i></a>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1731, 1, 1, 'common_quick_start', 'text_quick_start_checkout', '    	Below are most common settings for checkout process. \n    	More settings are available <a href=\"%s\" target=\"_new_admin\"><i class=\"fa fa-link fa-fw\"></i></a>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1732, 1, 1, 'common_quick_start', 'text_quick_start_appearance', '    	Below are most common settings for storefront appearance. \n    	More settings are available <a href=\"%s\" target=\"_new_admin\"><i class=\"fa fa-link fa-fw\"></i></a>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1733, 1, 1, 'common_quick_start', 'text_quick_start_mail', '    	Below are most common settings to enable email. If you use external email server (google, etc) to send emails, you need to use SMTP option. For emails to be sent from this server use Mail option. \n    	More settings are available <a href=\"%s\" target=\"_new_admin\"><i class=\"fa fa-link fa-fw\"></i></a>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1734, 1, 1, 'common_quick_start', 'text_quick_start_last', '    	Bellow you can select desired payments and shipping methods, additional languages and many other features to extend your eCommerce functionality.\n    	Remember to check a selection of extensions on <a href=\"#admin#rt=extension/extensions_store\" target=\"_extension\">AbanteCart marketplace</a> offered by community of professional developers.\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1735, 1, 1, 'common_quick_start', 'text_quick_start_last_footer', '    	You have completed quick starter wizard. <br /><br /> \n    	You can locate all the settings <a href=\"%s\">this link <i class=\"fa fa-link fa-fw\"></i></a>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1736, 1, 1, 'common_quick_start', 'payments_selection', '    <div class=\"info alert alert-info\">\n    <i class=\"fa fa-money fa-fw\"></i> Select your payment methods.\n    </div>\n    <div class=\"row\">\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=stripe\" target=\"_extension\">\n	        <img style=\"width: 94px; width: height: 94px;\" src=\"extensions/stripe/image/icon.png\"><br>Stripe</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=cardknox\" target=\"_extension\">\n	        <img style=\"width: 94px; width: height: 94px;\" src=\"extensions/cardknox/image/icon.png\"><br>CardKnox</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=paypal_commerce\" target=\"_extension\">\n	        <img style=\"width: 94px; width: height: 94px;\" src=\"extensions/paypal_commerce/image/icon.png\"><br>Paypal Commerce</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/payment\" target=\"_extension\">\n	        <img src=\"admin/view/default/image/default_extension.png\"><br>More Payments</a>\n	    </div>\n    </div>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1737, 1, 1, 'common_quick_start', 'shipping_selection', '    <div class=\"info alert alert-info\">\n    <i class=\"fa fa-truck fa-fw\"></i> Select your shipping methods.\n    </div>\n    <div class=\"row\">\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=default_free_shipping\" target=\"_extension\">\n	        <img src=\"extensions/default_free_shipping/image/icon.png\"><br>Free shipping</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=default_fedex\" target=\"_extension\">\n	        <img src=\"extensions/default_fedex/image/icon.png\"><br>Fedex</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=default_local_delivery\" target=\"_extension\">\n	        <img src=\"extensions/default_local_delivery/image/icon.png\"><br>Local Delivery</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/shipping\" target=\"_extension\">\n	        <img src=\"admin/view/default/image/default_extension.png\"><br>More Shippings</a>\n	    </div>\n    </div>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1738, 1, 1, 'common_quick_start', 'language_selection', '    <div class=\"info alert alert-info\">\n    <i class=\"fa fa-language fa-fw\"></i> Select additional languages.\n    </div>\n    <div class=\"row\">\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=default_italian\" target=\"_extension\">\n	        <img src=\"extensions/default_italian/image/icon.png\"><br>Italian</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=default_spanish\" target=\"_extension\">\n	        <img src=\"extensions/default_spanish/image/icon.png\"><br>Spanish</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions/install&extension=default_russian\" target=\"_extension\">\n	        <img src=\"extensions/default_russian/image/icon.png\"><br>Russian</a>\n	    </div>\n	    <div class=\"col-sm-3 h5 center\">\n	        <a href=\"#admin#rt=extension/extensions_store&category_id=67&sidx=date_modified\" target=\"_extension\">\n	        <img src=\"admin/view/default/image/default_extension.png\"><br>More Languages</a>\n	    </div>\n    </div>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
(1739, 1, 1, 'common_quick_start', 'more_extensions', '    <div class=\"info alert alert-info\">\n    <i class=\"fa fa-puzzle-piece fa-fw\"></i> More extensions on AbanteCart marketplace.\n    </div>\n    <div class=\"row center\">\n		<div class=\"btn-group center\">\n		    <a class=\"btn btn-primary\" href=\"#admin#rt=extension/extensions_store\" target=\"_extension\">\n		    	<i class=\"fa fa-cloud-download fa-fw\"></i> Extensions Marketplace\n		    </a>\n		</div>\n    </div>\n    ', '2024-08-12 13:49:47', '2024-08-12 13:49:47'),
